import React, { useEffect } from "react";
import { Table } from "@mantine/core";
import {
  useDeleteContactMutation,
  useGetContactQuery,
} from "../redux/api/contactApi";
import Cookies from "js-cookie";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { addContacts, setSearchTerm } from "../redux/services/ContactSlice";
import { Menu, Button, rem } from "@mantine/core";
import { useLogoutMutation } from "../redux/api/Auth";
import { removeUser } from "../redux/services/AuthSlice";

const ContactTable = () => {
  const token = Cookies.get("token");
  const { data, isLoading } = useGetContactQuery(token);
  const contacts = useSelector((state) => state.ContactSlice.contacts);
  const searchTerm = useSelector((state) => state.ContactSlice.searchTerm);
  const dispatch = useDispatch();
  const [deleteContact] = useDeleteContactMutation();
  const user = JSON.parse(Cookies.get("user"));
  const [logout] = useLogoutMutation();
  const nav = useNavigate();

  const removeContact = (id) => {
    try {
      deleteContact({ token, id: id });
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    dispatch(addContacts(data?.contacts?.data));
  }, [data]);

  const information = contacts?.filter((item) => {
    if (searchTerm === "") {
      return item;
    } else if (item?.name.toLowerCase().includes(searchTerm?.toLowerCase())) {
      return item;
    }
  });

  const logoutHandler = async () => {
    try {
      const { data } = await logout(token);
      if (data?.success) {
        dispatch(removeUser());
        nav("/login");
      }
    } catch (error) {
      console.log(error);
    }
  };

  if (isLoading) {
    return (
      <div className=" flex justify-center items-center h-screen">
        <p>Loading....</p>
      </div>
    );
  }

  return (
    <>
      <div className=" mx-auto flex shadow-lg my-7 p-7 items-center w-80 gap-3">
        <img
          src="data:image/png;base64,"
          alt=""
          width={"100px"}
        />
        <div className=" flex flex-col gap-2">
          <p className=" text-gray-500 ">{user?.name}</p>
          <p className=" text-gray-500">{user?.email}</p>
          <button
            onClick={logoutHandler}
            className=" text-white px-5 py-1 bg-red-600 hover:bg-red-500 rounded"
          >
            Logout
          </button>
        </div>
      </div>

      <div className=" mx-7 mt-16">
        <Table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Phone</th>
              <th>Address</th>
            </tr>
          </thead>
          <tbody>
            {information?.map((contact) => {
              return (
                <tr key={contact?.id}>
                  <td>{contact?.name}</td>
                  <td>
                    {contact?.email === null
                      ? "example@gmail.com"
                      : contact?.email}
                  </td>
                  <td>{contact?.phone}</td>
                  <td>
                    {contact?.address === null ? "Ohn Chaw" : contact?.address}
                  </td>
                  <td>
                    <Menu width={200} shadow="md">
                      <Menu.Target>
                        <Button variant="outline">...</Button>
                      </Menu.Target>

                      <Menu.Dropdown>
                        <Menu.Item
                          onClick={() => removeContact(contact?.id)}
                          className=" text-red-600 hover:text-red-500 cursor-pointer"
                          component="a"
                        >
                          <p>Delete</p>
                        </Menu.Item>
                        <Link to={`/user/${contact?.id}`}>
                          <Menu.Item>
                            <p>Detail</p>
                          </Menu.Item>
                        </Link>
                      </Menu.Dropdown>
                    </Menu>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
      </div>
    </>
  );
};

export default ContactTable;
